// 1AC, yes!!!class Solution {public:    void rotate(vector<vector<int> > &matrix) {        // IMPORTANT: Please reset any member data you declared, as        // the same Solution instance will be reused for each test case.        int n;                n = matrix.size();                if(n <= 0){            return;        }                int i, j, tmp;        int leni = n / 2;        int lenj = n - leni;                for(i = 0; i < leni; ++i){            for(j = 0; j < lenj; ++j){                // extra O(1)                tmp = matrix[i][j];                matrix[i][j] = matrix[n - 1 - j][i];                matrix[n - 1 - j][i] = matrix[n - 1 - i][n - 1 - j];                matrix[n - 1 - i][n - 1 - j] = matrix[j][n - 1 - i];                matrix[j][n - 1 - i] = tmp;            }        }    }};