// 2WA, 1AC, binary search + long long intclass Solution {public:    int sqrt(int x) {        // IMPORTANT: Please reset any member data you declared, as        // the same Solution instance will be reused for each test case.        long long int xx = x;                if(x <= 0){            return 0;        }else if(x < 4){            return 1;        }                long long int ll, rr, mm;        ll = 1;        rr = xx - 1;        while(rr - ll > 1){            mm = (ll + rr) / 2;            // 1WA here, wrong side of '<'            if(xx < mm * mm){                rr = mm;            }else{                ll = mm;            }        }                // 1WA here, return $ll, not $rr        return ll;    }};