// 1AC, integer overflow can be avoided by using '/' instead of '*'.class Solution {public:    int sqrt(int x) {        if(x <= 0){            return 0;        }else if(x < 4){            return 1;        }                long long int ll, rr, mm;        ll = 1;        rr = x - 1;        while(rr - ll > 1){            mm = (ll + rr) / 2;            if(x / mm < mm){                rr = mm;            }else{                ll = mm;            }        }                return ll;    }};